import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

export default function ShowDetail() {

    const [loading, setLoading] = useState(true);

    const { state } = useLocation();
    const [showDetails, setShowDetails] = useState([]);

    useEffect(() => {

        let mounted = true;
        axios.get("https://api.betaseries.com/shows/display", {
            params: {
                client_id: "22f661bdce5c",
                id: state
            }
        }).then(res => {
            if (mounted) {
                setShowDetails(res.data.show);
                setLoading(false);
            }
        }).catch(error => {
            console.log(error.response);
        })

        return function cleanup() {
            mounted = false;
        }

    }, [])

    return(
        <div className="container mx-auto mt-24 border border-red-500 text-white">
            {loading ? <p className="text-center mx-auto text-2xl font-cabin mt-28 uppercase font-bold">Chargement des informations...</p> :
            <div className="grid grid-cols-6 text-xl">
                <div className="col-span-1 text-right">
                    <div className="mb-7">
                        <p>Genre</p>
                        <p className="text-base text-gray-400">{Object.values(showDetails.genres).join(", ")}</p>
                    </div>
                    <div className="mb-7">
                        <p>Saisons</p>
                        <p className="text-base text-gray-400">{showDetails.seasons}</p>
                    </div>
                    <div className="mb-7">
                        <p>Episodes</p>
                        <p className="text-base text-gray-400">{showDetails.episodes}</p>
                    </div>
                    <div className="mb-7">
                        <p>Durée des épisodes</p>
                        <p className="text-base text-gray-400">{showDetails.length} min</p>
                    </div>
                    <div className="mb-7">
                        <p>Note</p>
                        <p className="text-base text-gray-400">{Math.round(showDetails.notes.mean)}/5</p>
                    </div>
                    <div className="mb-7">
                        <p>Archivable</p>
                        <p className="text-base text-gray-400">{showDetails.user.archived ? "Non" : "Oui"}</p>
                    </div>
                </div>
                <div className="col-span-1 text-right">
                    <h2 className="-mt-9 text-yellow-400 font-roboto text-2xl">{showDetails.title}</h2>
                    <img src={showDetails.images.show} />
                </div>
            </div>
            }
        </div>
    )
}
