/* This example requires Tailwind CSS v2.0+ */
import React, { useState, Fragment, useEffect } from 'react';
import { Disclosure, Menu, Transition } from '@headlessui/react';
import { MenuIcon, XIcon } from '@heroicons/react/outline';
import AuthService from '../../services/auth-service';
import axios from 'axios';
import { Link } from 'react-router-dom';

const navigation = [
  { name: 'Accueil', href: '/home', current: false },
  { name: 'Séries', href: '/series', current: false },
  { name: 'Amis', href: '/friends', current: false },
]

function classNames(...classes) {
  return classes.filter(Boolean).join(' ')
}

export default function Navbar() {

    const [avatar, setAvatar] = useState("");
    const [username, setUsername] = useState("");
    const [logged, setLogged] = useState(false);
    const [logoSource, setLogoSource] = useState("");

    useEffect(() => {
        const user = AuthService.getCurrentUser();
        //console.log(user.user.id);
        //console.log(currentUser);
        if (user !== null) {
            axios.get("https://api.betaseries.com/members/infos", {
                params: {
                    id: user.user.id,
                    client_id: "22f661bdce5c",
                }
            }).then(response => {
                setAvatar(response.data.member.avatar)
                setUsername(response.data.member.login)
                setLogoSource("./assets/CameraShutterBlack.svg")
                setLogged(true);
            })
            //setAvatar(response.data.member.avatar)       
        } else {
            setAvatar("./assets/utilisateur.svg");
            setUsername("Invité");
        }
    }, []);

    if (logged) {
    return (
    <Disclosure as="nav" className="bg-gradient-to-r from-transparent to-gray-400">
        {({ open }) => (
        <>
            <div className="max-w-7xl mx-auto px-2 sm:px-6 lg:px-8">
            <div className="relative flex items-center justify-between h-16">
                <div className="absolute inset-y-0 left-0 flex items-center sm:hidden">
                {/* Mobile menu button*/}
                <Disclosure.Button className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white">
                    <span className="sr-only">Open main menu</span>
                    {open ? (
                    <XIcon className="block h-6 w-6" aria-hidden="true" />
                    ) : (
                    <MenuIcon className="block h-6 w-6" aria-hidden="true" />
                    )}
                </Disclosure.Button>
                </div>
                <div className="flex-1 flex items-center justify-center sm:items-stretch sm:justify-start">
                <div className="flex-shrink-0 flex items-center">
                    <img
                    className="block lg:hidden h-8 w-auto"
                    src={logoSource}
                    alt="logo"
                    />
                    <img
                    className="hidden lg:block h-8 w-auto"
                    src={logoSource}
                    alt="logo"
                    />
                    <h1 className="text-white font-bold px-4">Previously On</h1>
                </div>
                <div className="hidden sm:block sm:ml-6">
                    <div className="flex space-x-4">
                    {navigation.map((item) => (/*
                        <a
                        key={item.name}
                        href={item.href}
                        className={classNames(
                            item.current ? 'bg-gray-700 text-white' : 'text-gray-300 hover:bg-gray-700 hover:text-white',
                            'px-3 py-2 rounded-md text-sm font-medium'
                        )}
                        aria-current={item.current ? 'page' : undefined}
                        >
                        {item.name}
                        </a>*/
                        <Link to={item.href}
                        key={item.name}
                        className={classNames(
                            item.current ? 'bg-gray-700 text-white' : 'text-gray-300 hover:bg-gray-700 hover:text-white',
                            'px-3 py-2 rounded-md text-sm font-medium'
                        )}
                        aria-current={item.current ? 'page' : undefined}
                        >{item.name}</Link>
                    ))}
                    </div>
                </div>
                </div>
                <div className="absolute inset-y-0 right-0 flex items-center pr-2 sm:static sm:inset-auto sm:ml-6 sm:pr-0">
                <p className="hidden sm:block bg-transparent p-1 text-white">{username}</p>

                {/* Profile dropdown */}
                <Menu as="div" className="ml-3 relative">
                    <div>
                    <Menu.Button className="bg-gray-800 flex text-sm rounded-full focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-white">
                        <span className="sr-only">Open user menu</span>
                        <img
                        className="h-8 w-8 rounded-full"
                        src={avatar}
                        alt="avatar"
                        />
                    </Menu.Button>
                    </div>
                    <Transition
                    as={Fragment}
                    enter="transition ease-out duration-100"
                    enterFrom="transform opacity-0 scale-95"
                    enterTo="transform opacity-100 scale-100"
                    leave="transition ease-in duration-75"
                    leaveFrom="transform opacity-100 scale-100"
                    leaveTo="transform opacity-0 scale-95"
                    >
                    <Menu.Items className="origin-top-right absolute right-0 mt-2 w-48 rounded-md shadow-lg py-1 bg-white ring-1 ring-black ring-opacity-5 focus:outline-none">
                        <Menu.Item>
                        {({ active }) => (
                            <a
                            href="#"
                            className={classNames(active ? 'bg-gray-100' : '', 'block px-4 py-2 text-sm text-gray-700')}
                            onClick={AuthService.logout}
                            >
                            Se déconnecter
                            </a>
                        )}
                        </Menu.Item>
                    </Menu.Items>
                    </Transition>
                </Menu>
                </div>
            </div>
            </div>

            <Disclosure.Panel className="sm:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1">
                {navigation.map((item) => (/*
                <a
                    key={item.name}
                    href={item.href}
                    className={classNames(
                    item.current ? 'bg-gray-900 text-white' : 'text-gray-300 hover:bg-gray-700 hover:text-white',
                    'block px-3 py-2 rounded-md text-base font-medium'
                    )}
                    aria-current={item.current ? 'page' : undefined}
                >
                    {item.name}
                </a>*/
                <Link 
                to={item.href}
                key={item.name}
                className={classNames(
                    item.current ? 'bg-gray-900 text-white' : 'text-gray-300 hover:bg-gray-700 hover:text-white',
                    'block px-3 py-2 rounded-md text-base font-medium'
                    )}
                aria-current={item.current ? 'page' : undefined}>
                {item.name}
                </Link>
                ))}
            </div>
            </Disclosure.Panel>
        </>
        )}
    </Disclosure>
    )
    } else {
        return (
            <Disclosure as="nav" className="bg-gradient-to-r from-transparent to-gray-400">
                {({ open }) => (
                <>
                    <div className="max-w-7xl mx-auto px-2 sm:px-6 lg:px-8">
                    <div className="relative flex items-center justify-between h-16">
                        <div className="absolute inset-y-0 left-0 flex items-center sm:hidden">
                        {/* Mobile menu button*/}
                        </div>
                        <div className="flex-1 flex items-center justify-center sm:items-stretch sm:justify-start">
                        <div className="flex-shrink-0 flex items-center">
                            <img
                            className="block lg:hidden h-8 w-auto"
                            src="./assets/CameraShutterBlack.svg"
                            alt="Previously On"
                            />
                            <img
                            className="hidden lg:block h-8 w-auto"
                            src="./assets/CameraShutterBlack.svg"
                            alt="Previously On"
                            />
                            <h1 className="text-white font-bold px-4">Previously On</h1>
                        </div>
                        <div className="hidden sm:block sm:ml-6">
                            <div className="flex space-x-4">
                            </div>
                        </div>
                        </div>
                        <div className="absolute inset-y-0 right-0 flex items-center pr-2 sm:static sm:inset-auto sm:ml-6 sm:pr-0">
                        <p className="hidden sm:block bg-transparent p-1 text-white">{username}</p>

                        {/* Profile dropdown */}
                        <Menu as="div" className="ml-3 relative">
                            <div>
                            <Menu.Button className="bg-gray-800 flex text-sm rounded-full focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-white">
                                <span className="sr-only">Open user menu</span>
                                <img
                                className="h-8 w-8 rounded-full"
                                src={avatar}
                                alt="Avatar"
                                />
                            </Menu.Button>
                            </div>
                        </Menu>
                        </div>
                    </div>
                    </div>

                    <Disclosure.Panel className="sm:hidden">
                    <div className="px-2 pt-2 pb-3 space-y-1">
                    </div>
                    </Disclosure.Panel>
                </>
                )}
            </Disclosure>
        )  
    }

}
