import React, { useEffect, useState } from "react";
import axios from "axios";

export default function ShowList() {

    const [showListing, setShowListing] = useState([]);
    const [smallDesc, setSmallDesc] = useState("");

    axios.get("https://api.betaseries.com/shows/list", {
        params: {
            client_id: "22f661bdce5c",
            order: "popularity",
            limit: "30",
        }
    }).then(res => {
        setShowListing(res.data.shows);
        setSmallDesc(res.data.shows.description.substring(0, 50) + " ...");
    }).catch(error => {
        console.log(error.response)
    })


    return (
        <div className="container mx-auto border text-white border-red-500">
            <div className="grid grid-cols-3">
                <div className="col-span-1">
                    <div className="border">Coucou</div>
                    <div className="border border-green-500">Coucou 2</div>
                </div>
                <div className="grid gap-x-8 gap-y-8 gap col-span-2 grid-cols-2">
                    {showListing.map(function(data) {
                        return(
                            <div className="border border-blue-700 font-roboto">
                                <img src={data.images.poster} href="Affiche de sÃ©rie" className="float-left w-2/5 mr-2"/>
                                <div className="infos-thumb-show my-1">
                                    <p className="border text-xl">{data.title}</p>
                                    <p className="border border-purple-400">{smallDesc}</p>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </div>
        </div>
    );
}
