import React, { useState, useEffect } from "react";
import {Â useLocation } from "react-router-dom";
import axios from "axios";

export default function ShowDetail() {

    const { state } = useLocation();
    const [showDetails, setShowDetails] = useState([]);

    useEffect(() => {
        console.log(state);

        axios.get("https://api.betaseries.com/shows/display", {
            params: {
                client_id: "22f661bdce5c",
                id: state
            }
        }).then(res => {
            console.log(res.data.show);
            setShowDetails(res.data.show);
        }).catch(error => {
            console.log(error.response);
        })
    }, [])

    return(
        <div className="container mx-auto mt-24 border border-red-500 text-white">
            <div className="grid grid-cols-6 text-xl">
                <div className="col-span-1">
                    <div className="text-right">
                        <p>Genre</p>
                        <p className="text-base text-gray-400">{Object.values(showDetails.genres).join(", ")}</p>
                    </div>
                </div>
            </div>
        </div>
    )
}
